            *input_type = h->input_type;
            MMI_multiline_inputbox.flags = h->flags;
            MMI_multiline_inputbox.ext_flags=h->ext_flags;
             /* barcode */
            MMI_multiline_inputbox.current_hilite_idx = h->current_hilite_idx;
